package com.rs.game.npc.glacor.attacks;import com.rs.game.Animation;import com.rs.game.Entity;import com.rs.game.Hit;import com.rs.game.Hit.HitLook;import com.rs.game.npc.glacor.Glacor;import com.rs.game.npc.glacor.GlacorAttacks;import com.rs.utils.Utils;/** *  *  *  * @author Tyler Represents The glacors melee attack. */public class MeleeAttack implements GlacorAttacks {	@Override	public int attack(Glacor glacor, Entity target) {		glacor.setNextAnimation(new Animation(9955));		target.applyHit(new Hit(target, Utils.random(312), HitLook.MELEE_DAMAGE));		return 5;	}}